mapper.select:
select o.ord_code, o.ord_name, o.ord_price, o.ord_status, p.payment_id, p.payment_method, p.payment_status, o.ord_regdate from order_tbl o inner join payment_tbl p on o.ord_code = p.ord_code
where o.ord_code > 0
order by o.ord_regdate desc limit #{cri.pageStart}, #{cri.perPageNum}

mapper.update:

 		update
 			order_tbl
 		set
 			ord_message = #{ord_message}
 		where 
			ord_code = #{ord_code}


 		update
 			order_tbl
 		set
 			ord_name = #{ord_name}, ord_addr_zipcode = #{ord_addr_zipcode}, ord_addr_basic = #{ord_addr_basic}, ord_addr_detail = #{ord_addr_detail}, ord_tel = #{ord_tel}, ord_mail = #{ord_mail}
 		where 
			ord_code = #{ord_code}

mapper.delete:

delete from
	ordetail_tbl
where
	ord_code = #{ord_code}
and
	pro_num = #{pro_num}

mapper.update:

 		update 
 			order_tbl
 		set
 			ord_price = #{ord_total_price}
 		where 
			ord_code = #{ord_code}


 		update 
 			payment_tbl
 		set
 			payment_price = #{ord_total_price}
 		where 
			ord_code = #{ord_code}


 		update 
 			order_tbl
 		set
 			ord_status = #{ord_status}
 		where 
			ord_code = #{ord_code}
